import {Component} from '@angular/core';
import {HttpClient} from '@angular/common/http';
import { SymbolSearchServiceService } from './symbol-search-service.service';
import {Observable} from "rxjs";

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})

export class AppComponent{
  title = 'Homework4';
  starter_url = 'https://www.alphavantage.co/query?function=';
  search_starter_url = 'https://www.alphavantage.co/query?function=SYMBOL_SEARCH&keywords=';
  stock_symbol = '';
  url = '';
  api_key = 'XGN36Q0ICTGL1ZWD';
  show_chart = false;
  chartData: Object = [];
  searchable = ''
  search_results: Observable<any[]>;
  constructor(private http: HttpClient,
              public symbolSearchService: SymbolSearchServiceService,
              this.) { } // used to access the service methods

  generateStockUrl(){
   // console.log("generateStockUrl() called");
    this.url = this.starter_url + 'TIME_SERIES_INTRADAY&symbol=' + this.stock_symbol + '&interval=5min&apikey=' + this.api_key;
  //  console.log("URL", this.url)

    this.http.get(this.url).subscribe((data) => {
      this.chartData = data;
      console.log("CHART DATA: ", this.chartData);
      this.show_chart = true;

  });
}

  search_results(input : string){
    let final_search_url = this.search_starter_url + input + '&apikey=' + this.api_key;
    console.log("input: ", input)
    return this.http.get(final_search_url)
  }

}
