import {Component, Input} from '@angular/core';

@Component({
  selector: 'app-chart',
  templateUrl: './chart.component.html',
  styleUrls: ['./chart.component.css']
})
export class ChartComponent  {

  @Input() data: any; // receive data from app app component

  view: any[] = [700, 400];
  // Customize color scheme as per your requirement
  colorScheme = {
    domain: ['#5AA454', '#A10A28', '#C7B42C', '#AAAAAA'],
  };
  showLegend = true;
  legendPosition = 'below';
  showXAxisLabel = true;
  showYAxisLabel = true;
  xAxisLabel = 'Time';
  yAxisLabel = 'Price';
  timeline = true;

}
<div style="display: block;" >
  <ngx-charts-line-chart>
    [view]="view"
    [scheme]="colorScheme"
    [results]="data"
    [legend]="showLegend"
    [legendPosition]="legendPosition"
    [showXAxisLabel]="showXAxisLabel"
    [showYAxisLabel]="showYAxisLabel"
    [xAxis]="xAxisLabel"
    [yAxis]="yAxisLabel"
    [timeline]="timeline"
  </ngx-charts-line-chart>
</div>
