import {Component} from '@angular/core';
import {HttpClient} from '@angular/common/http';
import {Observable} from "rxjs";

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})

export class AppComponent{
  title = 'Homework4';
  starter_url = 'https://www.alphavantage.co/query?function=';
  search_starter_url = 'https://www.alphavantage.co/query?function=SYMBOL_SEARCH&keywords=';
  stock_symbol = '';
  url = '';
  api_key = 'XGN36Q0ICTGL1ZWD';
  show_chart = false;
  chartData: Object = [];
  searchable = ''
  search_results: any[] = [];
  constructor(private http: HttpClient) { } // used to access the service methods

  generateStockUrl(){
   // console.log("generateStockUrl() called");
    this.url = this.starter_url + 'TIME_SERIES_INTRADAY&symbol=' + this.stock_symbol + '&interval=5min&apikey=' + this.api_key;
  //  console.log("URL", this.url)

    this.http.get(this.url).subscribe((data) => {
      this.chartData = data;
      console.log("CHART DATA: ", this.chartData);
      this.show_chart = true;

  });
}

  symbol_search(event: any) {
    let input = event.target.value;
    let final_search_url = this.search_starter_url + input + '&apikey=' + this.api_key;
    console.log("url: ", final_search_url);
    console.log("input: ", input);
    this.http.get(final_search_url).subscribe((data: any) => {
      this.search_results = data.bestMatches.map((match: any) => {
        return {
          symbol: match['1. symbol'],
          name: match['2. name']
        };
      });
    });
    console.log(this.search_results)
  }

}
