import { Component, Input, OnInit } from '@angular/core';

declare var google: any;

@Component({
  selector: 'app-chart',
  templateUrl: './chart.component.html',
  styleUrls: ['./chart.component.css']
})
export class ChartComponent implements OnInit {

  @Input() data: any;
  chart_name = '';

  constructor() {

  }

  ngOnInit(): void {
    google.charts.load('current', { packages: ['corechart'] });
    google.charts.setOnLoadCallback(() => this.drawChart());
  }

  drawChart(): void {
    const dataTable = new google.visualization.DataTable();
    dataTable.addColumn('string', 'Time');
    dataTable.addColumn('number', 'Open');
    dataTable.addColumn('number', 'High');
    dataTable.addColumn('number', 'Low');
    dataTable.addColumn('number', 'Close');
    dataTable.addRows(this.parseChartData());

    const chartOptions = {
      title: 'IBM Stock Price',
      height: 500,
      width: 900,
      legend: { position: 'bottom' }
    };

    const chart = new google.visualization.CandlestickChart(document.getElementById('chartContainer'));
    chart.draw(dataTable, chartOptions);
  }

  parseChartData(): any[] {
    const timeSeries = [];
    console.log("data", this.data);

    // stock
    if ('Time Series (5min)' in this.data){
      this.chart_name = this.data["Meta Data"] + 'stock price'
    const timeSeriesData = this.data['Time Series (5min)'];
    for (const key in timeSeriesData) {
      if (timeSeriesData.hasOwnProperty(key)) {
        const time = new Date(key).toISOString();
        const open = parseFloat(timeSeriesData[key]['1. open']);
        const high = parseFloat(timeSeriesData[key]['2. high']);
        const low = parseFloat(timeSeriesData[key]['3. low']);
        const close = parseFloat(timeSeriesData[key]['4. close']);

        timeSeries.push([time, open, high, low, close]);
      }
    }}
    // currency
    else if ('Time Series FX (Daily)'){
      const timeSeriesData = this.data['Time Series FX (Daily)'];
      for (const key in timeSeriesData) {
        if (timeSeriesData.hasOwnProperty(key)) {
          const time = new Date(key).toISOString();
          const open = parseFloat(timeSeriesData[key]['1. open']);
          const high = parseFloat(timeSeriesData[key]['2. high']);
          const low = parseFloat(timeSeriesData[key]['3. low']);
          const close = parseFloat(timeSeriesData[key]['4. close']);

          timeSeries.push([time, open, high, low, close]);
        }
      }
    }

    return timeSeries;
  }
}
